"""
Special ANSI codes.
"""

# Special
RESET = "\033[0m"

# Styles
BOLD = "\033[1m"
DIM = "\033[2m"
ITALIC = "\033[3m"
UNDERLINE = "\033[4m"
BLINK = "\033[5m"
INVERSE = "\033[7m"
NORMAL = "\033[22m"

# Foreground
FORE_BLACK = "\033[30m"
FORE_RED = "\033[31m"
FORE_GREEN = "\033[32m"
FORE_YELLOW = "\033[33m"
FORE_BLUE = "\033[34m"
FORE_MAGENTA = "\033[35m"
FORE_CYAN = "\033[36m"
FORE_WHITE = "\033[37m"
FORE_RESET = "\033[39m"
FORE_BRIGHT_BLACK = "\033[1;30m"
FORE_BRIGHT_RED = "\033[1;31m"
FORE_BRIGHT_GREEN = "\033[1;32m"
FORE_BRIGHT_YELLOW = "\033[1;33m"
FORE_BRIGHT_BLUE = "\033[1;34m"
FORE_BRIGHT_MAGENTA = "\033[1;35m"
FORE_BRIGHT_CYAN = "\033[1;36m"
FORE_BRIGHT_WHITE = "\033[1;37m"

# Background
