    def load_comments(self, room_id, page='1', limit='20'):
        """
        This method used to load comments in specific room

        :param room_id:
        :type room_id: int
        :param page: optional default=1
        :type page: int
        :param limit: optional default=20
        :type limit: int
        :return: it will return object named room
        """
        headers = {
            "QISCUS_SDK_SECRET": self.secret_key,
            "QISCUS_SDK_APP_ID": self.app_id
        }

        url = self.base_url + 'load_comments?room_id=' + \
            room_id + '&page=' + page + '&limit=' + limit
        requests = get(url, headers=headers)
        result = requests.json()
        if 200 <= requests.status_code < 400:
            comment = Response()
            comments_detail = []
            for i in result['results']['comments']:
                comments = Response()
                comments.comment_before_id = i['comment_before_id']
                comments.comments_disable_link_preview = i['disable_link_preview']
                comments.comments_user_email = i['email']
                comments.comments_id = i['id']
                comments.comments_message = i['message']
                comments.comments_room_id = i['room_id']
                comments.comments_room_name = i['room_name']
                comments.comments_timestamp = i['timestamp']
                comments.comments_unique_temp_id = i['unique_temp_id']
                comments.comments_user_avatar_url = i['user_avatar_url']
                comments.comments_username = i['username']
                comments.comments_info_as_json = json.dumps(result['results']['comments'])
                comments_detail.append(comments)
            comment.comments_info = comments_detail
            comment.comment_info_as_json = json.dumps(requests.json())
            comment.comment_status_code = requests.status_code
            return comment
        else:
            comment = Response()
            comment.error_message = result['error']['message']
            comment.error_info_as_json = json.dumps(requests.json())
            comment.error_status_code = requests.status_code
            return comment

    def search_messages(self, user_email, query, room_id=None):
        """
        This method used to search messages in specific room

        :param user_email:
        :type user_email: str
        :param query:keyword to search
        :type query: str
        :param room_id: optional, send this param if you want
        search message in specific room
        :type room_id: str
        :return: it will return object named room
        """
        headers = {
            "QISCUS_SDK_SECRET": self.secret_key,
            "QISCUS_SDK_APP_ID": self.app_id
        }
        data = {
            "user_email": user_email,
            "query": query,
            "room_id": room_id
        }

        url = self.base_url + 'search_messages'
        requests = post(url, data=data, headers=headers)
        result = requests.json()
        if 200 <= requests.status_code < 400:
            message = Response()
            message_details = []
            for i in result['results']['comments']:
                messages = Response()
                messages.comments_chat_type = i['chat_type']
                messages.comments_comment_before_id = i['comment_before_id']
                messages.comments_disable_link_preview = i['disable_link_preview']
                messages.comments_user_email = i['email']
                messages.comments_id = i['id']
                messages.comments_message = i['message']
                messages.comments_payload = i['payload']
                messages.comments_room_id = i['room_id']
                messages.comments_room_name = i['room_name']
                messages.comments_timestamp = i['timestamp']
                messages.comments_topic_id = i['topic_id']
                messages.comments_type = i['type']
                messages.comments_unique_temp_id = i['unique_temp_id']
                messages.comments_unix_timestamp = i['unix_timestamp']
                messages.comments_user_avatar_url = i['user_avatar_url']
                messages.comments_user_id = i['user_id']
                messages.comments_username = i['username']
                messages.comments_info_as_json = json.dumps(result['results']['comments'])
                message_details.append(messages)
            message.messages_info = message_details
            message.comments_info_as_json = json.dumps(requests.json())
            return message
        else:
            message = Response()
            message.error_message = result['error']['message']
            message.error_info_as_json = json.dumps(requests.json())
            message.error_status_code = requests.status_code
            return message

    def post_system_event_message(self, system_event_type, room_id, subject_email,
                                  object_email=None, updated_room_name=None):
        """
        This method used to post all type of system event message

        :param system_event_type: valid value is: "create_room", "add_member",
        "join_room", "remove_member", "left_room", "change_room_name", "change_room_avatar"
        :type system_event_type: str
        :param room_id: room id to post
        :type room_id: str
        :param subject_email: person who create a room, add member to room,
        join room, remove member from room, left from room,
        change room name and/or room avatar
        :type subject_email: str
        :param object_email: array of string. optional, only required when
        system event type is add_member or remove_member
        :type object_email: str
        :param updated_room_name: only required when system event message
        type is change_room_name or create_room
        :type updated_room_name: str
        :return: it will return object named room
        """
        headers = {
            "QISCUS_SDK_SECRET": self.secret_key,
            "QISCUS_SDK_APP_ID": self.app_id
        }

        data = {
            "system_event_type": system_event_type,
            "room_id": room_id,
            "subject_email": subject_email,
            "updated_room_name": updated_room_name
        }

        url = self.base_url + 'post_system_event_message'
        requests = post(url, data=data, headers=headers)
        result = requests.json()
        if 200 <= requests.status_code < 400:
            comment = Response()
            comment.comment_before_id = json.dumps(result['results']['comment']['comment_before_id'])
            comment.comment_disable_link_preview = json.dumps(result['results']['comment']['disable_link_preview'])
            comment.comment_user_email = json.dumps(result['results']['comment']['email'])
            comment.comment_id = json.dumps(result['results']['comment']['id'])
            comment.comment_message = json.dumps(result['results']['comment']['message'])
            comment.comment_payload = json.dumps(result['results']['comment']['payload'])
            comment.comment_room_id = json.dumps(result['results']['comment']['room_id'])
            comment.comment_status = json.dumps(result['results']['comment']['status'])
            comment.comment_timestamp = json.dumps(result['results']['comment']['timestamp'])
            comment.comment_topic_id = json.dumps(result['results']['comment']['topic_id'])
            comment.comment_type = json.dumps(result['results']['comment']['type'])
            comment.comment_unique_temp_id = json.dumps(result['results']['comment']['unique_temp_id'])
            comment.comment_unix_nano_timestamp = json.dumps(result['results']['comment']['unix_nano_timestamp'])
            comment.comment_unix_timestamp = json.dumps(result['results']['comment']['unix_timestamp'])
            comment.comment_user_avatar_url = json.dumps(result['results']['comment']['user_avatar_url'])
            comment.comment_user_id = json.dumps(result['results']['comment']['user_id'])
            comment.comment_username = json.dumps(result['results']['comment']['username'])
            comment.comment_info_as_json = json.dumps(requests.json())
            return comment
        else:
            comment = Response()
            comment.error_message = json.dumps(result['error']['message'])
            comment.error_status_code = json.dumps(requests.status_code)
            comment.error_as_json = json.dumps(requests.json())
            return comment
