#REQUIRES = ["connexion"]
REQUIRES = []
for file in ('./swagger_server/requirements.txt', './swagger_server/test-requirements.txt'):
    with open(file) as f:
        lines = f.readlines()
        lines = [l.strip() for l in lines]
    REQUIRES.append(lines)

setup(
    name=NAME,
    version=VERSION,
    description="Label Store Service",
    author_email="",
    url="",
    keywords=["Swagger", "Label Store Service"],
    install_requires=REQUIRES,
    packages=find_packages(),
    package_data={'': ['swagger/swagger.yaml']},
    include_package_data=True,
    long_description="""\
    No description provided (generated by Swagger Codegen https://github.com/swagger-api/swagger-codegen)
    """
)

